mapper.select:
SELECT SQL_CALC_FOUND_ROWS TAM.APPR_NO, (SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00026' AND E.CMM_CD = TAM.APPR_DIVS_CD) AS APPR_DIVS_CD_NM, APPR_NM, TEM.EMP_NM, (SELECT TEAMNM FROM T_DEPT_M TDM WHERE TDM.DEPT_NO = TEM.DEPT_NO) AS TEAMNM, DATE_FORMAT(TAM.FOR_APPR_DTM, '%Y.%m.%d %H:%i') AS FOR_APPR_DTM_DT, (SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00027' AND E.CMM_CD = TAM.APPR_STUS_CD) AS APPR_STUS_CD_NM FROM T_APPROVAL_M TAM, T_EMP_M TEM WHERE TAM.FOR_APPR_EMP_NO = TEM.EMP_NO AND TEM.EMP_NO =#{emp_no}
AND TAM.APPR_DIVS_CD = #{appr_divs_cd}
AND TAM.APPR_NM like CONCAT('%',#{appr_nm},'%')
LIMIT #{pageStart}, #{perPageNum}


  	SELECT APPR_NO
	FROM T_APPROVAL_M 
	WHERE APPR_NM =#{appr_nm}
	AND  APPR_REF_NO  =#{appr_ref_no}
	AND  FOR_APPR_EMP_NO  = #{emp_no}


SELECT APND_FILE_DIVS_CD, REG_FILE_NM
FROM T_APPROVAL_APND_FILE_D TAFD
WHERE TAFD.APPR_NO = #{appr_no}
