mapper.select:

    		SELECT 
				COALESCE(count(brand_id),0) CNT
			 FROM T_SUB_BRAND_M
			 WHERE BRAND_ID = #{masterBrandId}
			 AND SUB_BRAND_ID = #{subBrandId}


    		SELECT 
				BRAND_ID, SUB_BRAND_ID, SUB_BRAND_NM, LIQ_KD_CD, STCASE_CD, SORT_ORD, USE_YN
			 FROM T_SUB_BRAND_M
			 WHERE BRAND_ID = #{masterBrandId}
			 AND SUB_BRAND_ID = #{brandId}
