mapper.select:

    		SELECT 
				COALESCE(count(cmm_cd_grp_id),0) CNT
			 FROM T_CMM_CD_M
			 WHERE CMM_CD_GRP_ID = #{cmm_cd_grp_id}
			 AND CMM_CD = #{cmm_cd}


    		SELECT 
				CMM_CD_GRP_ID, CMM_CD, CMM_CD_NM, USE_YN
			 FROM T_CMM_CD_M
			 WHERE CMM_CD_GRP_ID = #{cmm_cd_grp_id}
			 AND CMM_CD = #{cmm_cd}
