mapper.select:

    		SELECT 
				COALESCE(count(cmm_cd_grp_id),0) CNT
			 FROM T_CMM_CD_M
			 WHERE CMM_CD_GRP_ID = #{cmm_cd_grp_id}
			 AND CMM_CD = #{cmm_cd}


    		SELECT 
				CMM_CD_GRP_ID, CMM_CD, CMM_CD_NM, CMM_CD_CNTN, USE_YN
			 FROM T_CMM_CD_M
			 WHERE CMM_CD_GRP_ID = #{cmm_cd_grp_id}
			 AND CMM_CD = #{cmm_cd}

mapper.update:

    		UPDATE T_CMM_CD_M
    			SET   CMM_CD_NM = #{cmm_cd_nm},
    				  CMM_CD_CNTN = #{cmm_cd_cntn},
    				  USE_YN = #{sub_use_yn},
    				  DATA_UPD_DTM = NOW(),
    				  DATA_UPD_ID = #{regId}
    	    WHERE CMM_CD_GRP_ID = #{cmm_cd_grp_id}
			 AND CMM_CD = #{cmm_cd}
