mapper.select:

    		SELECT  SQL_CALC_FOUND_ROWS
				A.PRPS_ID, 
				(SELECT TEAMNM FROM T_DEPT_M C WHERE C.DEPT_NO = B.DEPT_NO ) AS TEAMNM, 
				B.EMP_NM, 
				DATE_FORMAT(STR_TO_DATE(A.PRPS_STR_DT, '%Y%m%d'),'%Y.%m.%d ') AS PRPS_STR_DT,
				DATE_FORMAT(STR_TO_DATE(A.PRPS_END_DT, '%Y%m%d'),'%Y.%m.%d ') AS PRPS_END_DT,
				A.PRPS_NM,
				(SELECT D.OUTLET_NM FROM T_VENDOR_M D WHERE D.VENDOR_NO = A.OUTLET_NO) AS VD_NM,
				(SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00021' AND E.CMM_CD = A.PRPS_PURPOSE_CD) AS PRPS_PURPOSE_CD_NM,
				(SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00022' AND E.CMM_CD = A.ACT_PLAN_CD) AS ACT_PLAN_CD_NM,
				(SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00020' AND E.CMM_CD = A.PRPS_STUS_CD) AS PRPS_STUS_CD_NM,
				A.WHOLESALE_VENDOR_NO, A.OUTLET_NO, A.MARKET_DIVS_CD, A.PRPS_PURPOSE_CD,
				A.ACT_PLAN_CD, A.BASE_PRPS_AMT, A.LAST_PRPS_AMT, A.BUDG_AMT, A.CASERATE_AMT, 
				A.PRPS_REG_DT, A.PRPS_STUS_CD, A.VENDOR_SGMT_DIVS_CD
			FROM T_PROPOSAL_M A , T_EMP_M B
			WHERE A.EMP_NO = B.EMP_NO



				AND A.OUTLET_NO = #{outlet_no}




				AND B.DEPT_NO = #{deptno}




				AND B.EMP_NO =  #{empno}




				AND A.PRPS_PURPOSE_CD = #{prps_purpose_cd}




				AND A.ACT_PLAN_CD = #{act_plan_cd}




				AND A.PRPS_STUS_CD = #{prps_stus_cd}




				AND #{prps_str_dt} BETWEEN A.PRPS_STR_DT AND A.PRPS_END_DT
				AND #{prps_end_dt} BETWEEN A.PRPS_STR_DT AND A.PRPS_END_DT




				AND A.EMP_NO =#{emp_no}




			    AND B.DEPT_NO=  #{deptno}




			    AND B.DEPT_NO IN (SELECT DEPT_NO  FROM T_DEPT_EMP_REL_D WHERE EMP_NO =#{emp_no} AND EMP_DEPT_REL_GBN_CD ='0002')



		ORDER BY A.DATA_REG_DTM DESC



				 LIMIT #{pageStart}, #{perPageNum}

mapper.update:

    		UPDATE T_PROPOSAL_M
			SET 
			   PRPS_STR_DT = #{prps_str_dt}
			  ,PRPS_END_DT = #{prps_end_dt}
			  ,PRPS_NM = #{prps_nm}
			  ,PRPS_PURPOSE_CD = #{prps_purpose_cd}
			  ,ACT_PLAN_CD =#{act_plan_cd}
			  ,BASE_PRPS_AMT = #{base_prps_amt}
			  ,LAST_PRPS_AMT = #{last_prps_amt}
			  ,BUDG_AMT = #{ budg_amt}
			  ,CASERATE_AMT = #{caserate_amt}
			  ,PRPS_STUS_CD = #{prps_stus_cd}
			  ,PRPS_CNTN = #{prps_cntn}
			  ,DATA_UPD_DTM =  now()
			  ,DATA_UPD_ID =  #{login_id}
			WHERE PRPS_ID =  #{prps_id}

mapper.select:

  	SELECT PRPS_ID
	FROM T_PROPOSAL_M 
	WHERE OUTLET_NO =#{outlet_no}
	AND EMP_NO = #{emp_no}
	ORDER BY DATA_REG_DTM DESC LIMIT 1

mapper.update:

    	INSERT INTO T_PROPOSAL_PROD_D(
   			PRPS_ID, PROD_SITEM_DIVS_CD, PROD_NO_SITEM_NM, DELIVERY_CNT, DELIVERY_AMT,
  			DC_RATE, LAST_DELIVER_AMT, DATA_REG_DTM, DATA_REG_ID, DATA_UPD_DTM, DATA_UPD_ID
  		)VALUES (
			#{prps_id}, #{prod_sitem_divs_cd}, #{prod_no_sitem_nm}, #{delivery_cnt}, #{delivery_amt},
			 #{dc_rate}, #{last_deliver_amt}, now(), #{login_id}, now(), #{login_id}
		 )

mapper.select:

SELECT PLAN_DELIVERY_CNT, REAL_DELIVERY_CNT, REAL_DELIVERY_CNTN
FROM T_PROPOSAL_PROD_MON_D
WHERE PRPS_ID = #{prpsId}
AND PROD_SITEM_DIVS_CD = #{prodSitemDivsCd}
AND PROD_NO_SITEM_NM = #{prodNoSitemNm}
AND DELIVERY_MON = #{deliDate}

SELECT A.PRPS_ID, (SELECT TEAMNM FROM T_DEPT_M C WHERE C.DEPT_NO = B.DEPT_NO ) AS TEAMNM, B.EMP_NM, DATE_FORMAT(STR_TO_DATE(A.PRPS_STR_DT, '%Y%m%d'),'%Y.%m.%d ') AS PRPS_STR_DT, DATE_FORMAT(STR_TO_DATE(A.PRPS_END_DT, '%Y%m%d'),'%Y.%m.%d ') AS PRPS_END_DT, A.PRPS_NM, (SELECT D.OUTLET_NM FROM T_VENDOR_M D WHERE D.VENDOR_NO = A.OUTLET_NO) AS VD_NM, (SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00021' AND E.CMM_CD = A.PRPS_PURPOSE_CD) AS PRPS_PURPOSE_CD_NM, (SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00022' AND E.CMM_CD = A.ACT_PLAN_CD) AS ACT_PLAN_CD_NM, (SELECT E.CMM_CD_NM FROM T_CMM_CD_M E WHERE E.CMM_CD_GRP_ID='00020' AND E.CMM_CD = A.PRPS_STUS_CD) AS PRPS_STUS_CD_NM, A.WHOLESALE_VENDOR_NO, A.OUTLET_NO, A.MARKET_DIVS_CD, A.PRPS_PURPOSE_CD, A.ACT_PLAN_CD, A.BASE_PRPS_AMT, A.LAST_PRPS_AMT, A.BUDG_AMT, A.CASERATE_AMT, A.PRPS_REG_DT, A.PRPS_STUS_CD, A.VENDOR_SGMT_DIVS_CD FROM T_PROPOSAL_M A , T_EMP_M B WHERE A.EMP_NO = B.EMP_NO AND A.OUTLET_NO = #{vendor_no}
AND #{str_dt} BETWEEN A.PRPS_STR_DT AND A.PRPS_END_DT AND #{end_dt} BETWEEN A.PRPS_STR_DT AND A.PRPS_END_DT
AND A.EMP_NO =#{emp_no}
AND B.DEPT_NO= #{deptno}
AND B.DEPT_NO IN (SELECT DEPT_NO FROM T_DEPT_EMP_REL_D WHERE EMP_NO =#{emp_no} AND EMP_DEPT_REL_GBN_CD ='0002')
ORDER BY A.DATA_REG_DTM DESC
